func parenthesisFloors(input string) int {
	floor := 0
	var openPar byte = '('
	var closedPar byte = ')'
	for i := 0; i < len(input); i++ {
		currChar := input[i]
		if currChar == openPar {
			floor++
		} else if currChar == closedPar {
			floor--
		} else {
			fmt.Println("invalid character")
		}
		if floor == -1 {
			return i+1
		}
	}
	return 0
}